mapper.select:
		SELECT visit_date, ip_address
		FROM   visit_logs
		WHERE  visit_date = #{visitDate}
          AND  ip_address = #{ip}

mapper.update:

	SELECT IFNULL(visit_count, 0)  -- visit_count 최대값 조회(null 이면 기본값 0 지정) 
	FROM daily_visit
	WHERE visit_date = #{visit_date}

UPDATE daily_visit
SET visit_count = #{visit_count} + 1
WHERE visit_date = #{visit_date}
