mapper.insert:
INSERT INTO MEMBER (
	  member_name
	, member_email
	, member_pwd
	, member_tel
	, member_status
	, member_marketing1
	, member_marketing2
	, member_reg_date
	, member_type
	, member_img
	, member_social
	, member_addr
	, member_nickname
	, member_credit
)
VALUES (
	  #{member_name}
	, #{member_email}
	, #{member_pwd}
	, #{member_tel}
	, 1
	, #{member_marketing1}
	, #{member_marketing2}
	, #{member_reg_date}
	, 1
	, #{member_img}
	, #{member_social}
	, #{member_addr}
	, #{member_name}
	, 0
)

mapper.update:
UPDATE MEMBER
SET member_pwd = #{member_pwd}
WHERE member_email = #{member_email}

UPDATE MEMBER
SET member_status = 1
WHERE member_email = #{member_email}

mapper.select:
SELECT member_tel
FROM MEMBER
WHERE member_tel = #{member_tel}
